targets.query:
from(bucket: "${bucket}")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_field"] == "process_start_time_seconds")
  |> filter(fn: (r) => r["host"] == "${hostname}")
  |> last()
  |> map(fn: (r) => ({
    r with _value: 
    uint(v:
      duration(v:
        int(v: 
          now()
        ) - int(v: 
          r._value * 1000.0
        ) * 1000000
      )
    )
  }))
